function ZDAT.Data.Quests.isDailyQuest(questName, zone)
    for i, v in ipairs(ZDAT.Data.Quests.DB) do
      for j, y in ipairs(v.QUEST_IDS) do
        local questNameFromId = GetQuestName(y)
        -- Both The Reach and Western Skyrim has the same quest "Halt the Harrowstorms" so need to check zone
        if questName == "Halt the Harrowstorms" and zone ~= nil and zone == "The Reach" then
          --d(ZDAT.name " > [Halt the Harrowstorms] of The Reach is added to the tracker.")
          return 2953
        elseif questNameFromId == questName then
          return v.ACHIEVEMENT_ID
        end
      end
    end
    return -1
end